<div
    x-cloak
    x-data="{}"
    class="mx-auto w-full max-w-screen-lg px-5 pt-40"
>
    <div
        x-data="{}"
        x-ref="sponsors_section"
        x-init="
            () => {
                if (reducedMotion) return
                gsap.timeline({
                    scrollTrigger: {
                        trigger: $refs.sponsors_section,
                        start: 'top bottom-=200px',
                    },
                })
                    .fromTo(
                        $refs.sponsors_section,
                        {
                            autoAlpha: 0,
                            x: -50,
                        },
                        {
                            autoAlpha: 1,
                            x: 0,
                            duration: 0.7,
                            ease: 'circ.out',
                        },
                    )
                    .fromTo(
                        $refs.premium,
                        {
                            autoAlpha: 0,
                            x: 50,
                        },
                        {
                            autoAlpha: 1,
                            x: 0,
                            duration: 0.7,
                            ease: 'circ.out',
                        },
                        '>-0.5',
                    )
                    .fromTo(
                        $refs.premium_heart,
                        {
                            autoAlpha: 0,
                            scale: 0,
                        },
                        {
                            autoAlpha: 1,
                            scale: 1,
                            duration: 0.7,
                            ease: 'back.out',
                        },
                        '>-0.6',
                    )
                    .fromTo(
                        $refs.sponsors_header,
                        {
                            autoAlpha: 0,
                            x: -50,
                        },
                        {
                            autoAlpha: 1,
                            x: 0,
                            duration: 0.7,
                            ease: 'circ.out',
                        },
                        '<',
                    )
                    .fromTo(
                        $refs.sponsors_header_underline,
                        {
                            autoAlpha: 0,
                            scaleX: 0,
                            transformOrigin: 'left',
                        },
                        {
                            autoAlpha: 1,
                            scaleX: 1,
                            duration: 0.5,
                            ease: 'circ.out',
                        },
                        '>-0.5',
                    )
                    .fromTo(
                        $refs.message,
                        {
                            autoAlpha: 0,
                            y: 50,
                        },
                        {
                            autoAlpha: 1,
                            y: 0,
                            duration: 0.7,
                            ease: 'circ.out',
                        },
                        '<',
                    )
                    .fromTo(
                        $refs.sponsors_list.querySelectorAll('a > *'),
                        {
                            autoAlpha: 0,
                            scale: 0,
                        },
                        {
                            autoAlpha: 1,
                            scale: 1,
                            duration: 0.7,
                            ease: 'back.out',
                            stagger: 0.1,
                        },
                        '<',
                    )
                    .fromTo(
                        $refs.sponsor_button,
                        {
                            autoAlpha: 0,
                            y: 50,
                        },
                        {
                            autoAlpha: 1,
                            y: 0,
                            duration: 0.7,
                            ease: 'circ.out',
                        },
                        '<',
                    )
                    .fromTo(
                        $refs.big_heart_top_right,
                        {
                            autoAlpha: 0,
                            scale: 0,
                        },
                        {
                            autoAlpha: 1,
                            scale: 1,
                            duration: 0.7,
                            ease: 'back.out',
                        },
                        '<',
                    )
                    .fromTo(
                        $refs.big_heart_top_left,
                        {
                            autoAlpha: 0,
                            y: 100,
                            x: -20,
                        },
                        {
                            autoAlpha: 1,
                            y: 0,
                            x: 0,
                            duration: 1,
                            ease: 'sine.out',
                        },
                        '<',
                    )
                    .fromTo(
                        $refs.big_heart_bottom_right,
                        {
                            autoAlpha: 0,
                            y: 100,
                            x: 20,
                        },
                        {
                            autoAlpha: 1,
                            y: 0,
                            x: 0,
                            duration: 1,
                            ease: 'sine.out',
                        },
                        '>-0.5',
                    )
                    .fromTo(
                        $refs.mini_heart_1,
                        {
                            autoAlpha: 0,
                            y: 50,
                            x: 20,
                        },
                        {
                            autoAlpha: 1,
                            y: 0,
                            x: 0,
                            duration: 1,
                            ease: 'sine.out',
                        },
                        '<',
                    )
                    .fromTo(
                        $refs.mini_heart_2,
                        {
                            autoAlpha: 0,
                            y: 50,
                            x: 20,
                        },
                        {
                            autoAlpha: 1,
                            y: 0,
                            x: 0,
                            duration: 1,
                            ease: 'sine.out',
                        },
                        '<',
                    )
                    .fromTo(
                        $refs.mini_heart_3,
                        {
                            autoAlpha: 0,
                            y: 50,
                            x: 20,
                        },
                        {
                            autoAlpha: 1,
                            y: 0,
                            x: 0,
                            duration: 1,
                            ease: 'sine.out',
                        },
                        '<',
                    )
            }
        "
        class="relative z-[1] overflow-hidden rounded-3xl bg-gradient-to-tl from-[#CDD0E5] to-[#FFECEF] px-5 pb-32"
    >
        {{-- Title --}}
        <div class="grid place-items-center pt-14">
            <div
                x-ref="premium"
                class="relative text-xl font-medium tracking-wider"
            >
                Our Premium
                <div
                    x-ref="premium_heart"
                    class="absolute -left-9 -top-2"
                >
                    <svg
